import { IngredientListService } from './../../service/ingredient-list.service';
import { Component } from '@angular/core';
import { IngredientList } from 'src/app/models/ingredient-list';
import { FormArray, FormGroup, FormBuilder, FormControl } from '@angular/forms';
import { IngredientListService } from 'src/app/service/ingredient.service';

@Component({
  selector: 'app-ingredient-list-form',
  templateUrl: './ingredient-list-form.component.html',
  styleUrls: ['./ingredient-list-form.component.css']
})
export class IngredientListFormComponent {

dishForm = FormGroup;

  constructor( private ingredientListService: IngredientService, private fb: FormBuilder) {
    this.dishForm = this.fb.group({
      title: [''],
      ingredientLists: this.fb.array([])
    });
  }
  ngOnInit(): void {}
  get ingredientLists(): FormArray {
    return this.dishForm.get('ingredientLists') as FormArray;
  }

}
