import { DishService } from './../../service/dish.service';
import { Dish } from './../../models/dish';
import { IngredientListService } from './../../service/ingredient-list.service';
import { Component } from '@angular/core';
import { IngredientList } from 'src/app/models/ingredient-list';
import { FormArray, FormGroup, FormBuilder, FormControl, Validators } from '@angular/forms';



@Component({
  selector: 'app-ingredient-list-form',
  templateUrl: './ingredient-list-form.component.html',
  styleUrls: ['./ingredient-list-form.component.css']
})
export class IngredientListFormComponent {

dishForm = FormGroup;

constructor(private fb: FormBuilder) {
  this.dishForm = this.fb.group({
    id: [null, Validators.required],
    time: [null, Validators.required],
    difficulty: [null, Validators.required],
    rating: [null, Validators.required],
    title: ['', Validators.required],
    ingredientLists: this.fb.array([
      this.createIngredientFormGroup() // Initial empty ingredient
    ])
  });
}

createIngredientFormGroup(): FormGroup {
  return this.fb.group({
    ingredientName: ['', Validators.required],
    quantity: [null, Validators.required]
  });
}

addIngredient(): void {
  (this.dishForm.get('ingredientLists') as FormArray).push(this.createIngredientFormGroup());
}

// Method to remove an ingredient
removeIngredient(index: number): void {
  (this.dishForm.get('ingredientLists') as FormArray).removeAt(index);
}

// Method to handle form submission
onSubmit() {
  if (this.dishForm.valid) {
    console.log(this.dishForm.value);
    // Handle the form submission logic here
  }
}
}
