import { Component } from '@angular/core';
import { FormBuilder, FormGroup, FormControl, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { IngredientService } from 'src/app/service/ingredient.service';
import { Ingredient } from 'src/app/models/ingredient';

@Component({
  selector: 'app-ingredient-form',
  templateUrl: './ingredient-form.component.html',
  styleUrls: ['./ingredient-form.component.css'],
})

export class IngredientFormComponent {
ingredientsForm = new FormGroup({
  ingredientName: new FormControl(''),
});
ingredientId: number | null = null;

constructor (private ingredientService: IngredientService){}
ngOnInit():void {
  this.loadIngredientList();
}
onSubmit() {
  if (this.ingredientsForm.valid) {
    const ingredient: Ingredient = this.ingredientsForm.value;
    this.IngredientService.addIngredient(ingredient).subscribe(() => {
      this.router.navigate(['/']);
    });
}
}
}
